// Video: 15 jQuery Append & Prepend Method:     ~~~Notes~~~



// Append() Method:
// HTML k element k inside last pe agr hum ne koi new element/tag add krna hai to usy hum append() method se add krty hen.
$('.append-method-btn').click(function(){
    $('.append-method-box').append('<h4> Using jQuery Append Method: </h4> ')
});



// Prepend() Method:
// HTML k element k inside start pe agr hum ne koi new element/tag add krna hai to usy hum prepend() method se add krty hen.
$('.prepend-method-btn').click(function(){
    $('.append-method-box').prepend('<h4> Using jQuery Prepend Method: </h4> ')
});
